using Krosoft.Extensions.Core.Helpers;
using Krosoft.Extensions.Core.Models.Exceptions;
using Microsoft.VisualStudio.TestTools.UnitTesting;
using NFluent;

namespace Krosoft.Extensions.Core.Tests.Helpers;

[TestClass]
public class ImageHelperTests
{
    [TestMethod]
    public void ImageKoBase64Test()
    {
        Check.ThatCode(() => ImageHelper.VerifierImage("", "test.png"))
             .Throws<KrosoftTechniqueException>()
             .WithMessage("Impossible de récupérer l'image à partir de l'url Base64 pour l'image test.png.");
    }

    [TestMethod]
    public void ImageOkBase64Test()
    {
        var base64 =
            "data:image/png;base64,";

        ImageHelper.VerifierImage(base64, "test.png");
    }
}